mapper.select:
SELECT T3.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY T1.REQ_DT DESC) AS ROWNUM , T1.REQ_DT /* 예약일자 */ , T1.SEQ /* 일련번호 */ , T1.USER_ID /* 사용자ID */ , T1.REQ_HAN_NM /* 예약자한글명 */ , T1.REQ_ENG_NM /* 예약자영문명 */ , T1.REQ_TEL_NO /* 예약자전화번호 */ , T1.MEM_GBN /* 회원구분 */ , (SELECT CODE FROM BC_COMM_L WHERE 1 = 1 AND HEAD_CD = '500030' AND CODE = T1.MEM_GBN) AS MEM_CD /* 멤버구분코드 */ , (SELECT CODE_NM FROM BC_COMM_L WHERE 1 = 1 AND HEAD_CD = '500030' AND CODE = T1.MEM_GBN) AS MEM_NM /* 멤버구분 */ , (SELECT CODE_NM FROM BC_COMM_L WHERE 1 = 1 AND HEAD_CD = '500020' AND CODE = T1.PRC_STS) AS STATE_NM /* 상태구분 */ , T1.AGN_CD /* 에이전시코드 */ , T1.PRC_STS /* 예약상태 */ , T1.CHK_IN_DT /* 체크인일자 */ , T1.CHK_OUT_DT /* 체크아웃일자 */ , T1.ROOM_TYPE /* 객실타입 */ , T1.FLIGHT_IN /* 도착항공기편 */ , T1.FLIGHT_OUT /* 출발항공기편 */ , T1.TOT_PERSON /* 총인원 */ , T1.BAS_YY /* 기준년도 */ , T1.PROD_SEQ /* 상품순번 */ , T1.PICK_GBN /* 미팅샌딩구분 */ , T1.ADD_R_S_PER /* 싱글룸추가인원수 */ , T1.ADD_R_S_DAY /* 싱글룸추가일수 */ , T1.ADD_R_S_CNT /* 싱글룸추가갯수 */ , T1.ADD_R_P_PER /* 프리미엄룸추가일수 */ , T1.ADD_R_P_DAY /* 프리미엄룸추가일수 */ , T1.ADD_R_P_CNT /* 프리미엄추가갯수 */ , T1.CNT_D1 /* 트윈갯수 */ , T1.CNT_D2 /* 더블갯수 */ , T1.CNT_P1 /* 프리미엄트윈갯수 */ , T1.CNT_P2 /* 프리미엄킹갯수 */ , T1.PICK_IN /* 픽업차량-도착 */ , T1.PICK_OUT /* 픽업차량-출발 */ , T1.LATE_CHECK_OUT /* Late체크아웃여부 */ , T1.REMARK /* 비고(추가요구사항) */ , T1.INV_REG_DT /* 인보이스발행일자 */ , T1.CONFIRM_NO /* 리조트confirm번호 */ , T1.RND_CHG_YN1 /* 라운딩변경(토오전)여부*/ , T1.RND_CHG_YN2 /* 라운딩변경(일오전)여부*/ , T1.REG_DTM /* 등록일시 */ , T1.REG_ID /* 등록자ID */ , T1.UPD_DTM /* 수정일시 */ , T1.UPD_ID /* 수정자ID */ , ( SELECT TT1.CODE FROM BC_COMM_L TT1 , TB_PROD_INFO TT2 WHERE 1 = 1 AND TT2.BAS_YY = T1.BAS_YY AND TT2.PROD_SEQ = T1.PROD_SEQ AND TT1.CODE = TT2.HDNG_GBN AND HEAD_CD = '500000' ) AS PROD_CD /* 상품코드 */ , ( SELECT TT1.CODE_NM FROM BC_COMM_L TT1 , TB_PROD_INFO TT2 WHERE 1 = 1 AND TT2.BAS_YY = T1.BAS_YY AND TT2.PROD_SEQ = T1.PROD_SEQ AND TT1.CODE = TT2.HDNG_GBN AND HEAD_CD = '500000' ) AS PROD_NM /* 상품명 */ , T2.DEP_IN_DT /* 예약금입금일자 */ , NVL(T2.DEP_AMT , 0) AS DEP_AMT /* 예약금액 */ , NVL(T2.BAL_AMT , 0) AS BAL_AMT /* 잔금 */ , NVL(T2.DCT_AMT , 0) AS DCT_AMT /* 할인금액 */ , NVL(T2.PAY_AMT , 0) AS PAY_AMT /* 입금금액 */ , NVL(T2.TOT_AMT , 0) AS TOT_AMT /* 총액 */ FROM TB_REQ_BOOKING_M T1 LEFT OUTER JOIN TB_REQ_FEE T2 ON T2.REQ_DT = T1.REQ_DT AND T2.REQ_SEQ = T1.SEQ WHERE 1 = 1
AND ${SEARCH} BETWEEN #{RESERVE_STDT} AND #{RESERVE_EDDT}
) T3 WHERE 1 = 1
AND T3.MEM_GBN = #{MEM_GBN}
AND T3.PRC_STS = #{PRC_STS}
ORDER BY T3.ROWNUM

	    SELECT T1.CODE , T1.CODE_NM
          FROM BC_COMM_L T1
          JOIN BC_COMM_H T2 ON T2.HEAD_CD = /**/ T1.HEAD_CD
         WHERE 1 = 1
           AND T1.HEAD_CD = #{HEAD_CD}

	  	   AND T1.REF_CHR1 = #{REF_CHR1}


	  	   AND T1.CODE > #{CODE}



	           ORDER BY T1.REF_CHR5


               ORDER BY T1.CODE

SELECT COUNT(1) AS CNT
  FROM TB_REQ_INVOICE T1
 WHERE 1 = 1
   AND REQ_DT = #{REQ_DT}
   AND SEQ    = #{SEQ}

mapper.insert:
INSERT INTO TB_REQ_INVOICE
            ( REQ_DT
            , SEQ
            , ITEM_CD
            , ITEM_NM
            , AMT_SIGN
            , PER_AMT
            , USE_DAY
            , UNIT_DAY
            , USE_NUM
            , UNIT_NUM
            , TOT_AMT
            , TB_REQ_INVOICE.ORDER
            , REF1
            , REF2
            , REG_DTM
            , REG_ID
            , UPD_DTM
            , UPD_ID
            )
     VALUES ( #{REQ_DT  }
            , #{SEQ     }
            , #{ITEM_CD }
            , #{ITEM_NM }
            , #{AMT_SIGN}
            , #{PER_AMT }
            , #{USE_DAY }
            , CASE WHEN #{STR_UNIT_DAY} = '일' THEN 'D' WHEN #{STR_UNIT_DAY} = '회' THEN 'C' ELSE NULL END
            , #{USE_NUM }
            , CASE WHEN #{STR_UNIT_NUM} = '인' THEN 'P' WHEN #{STR_UNIT_NUM} = '개' THEN 'R' ELSE NULL END
            , #{TOT_AMT }
            , #{ORDER   }
            , NULL
            , NULL
            , NOW()
            , #{LOGIN_USER}
            , NOW()
            , #{LOGIN_USER}
            )

INSERT INTO TB_REQ_INVOICE_HIS
