            , REF1
            , REF2
            , REG_DTM
            , REG_ID
            , UPD_DTM
            , UPD_ID
            )
     VALUES ( #{REQ_DT  }
            , #{SEQ     }
            , (SELECT NVL(MAX(HIS_SEQ),0) AS SEQ FROM TB_REQ_INVOICE_HIS T1 WHERE REQ_DT = #{REQ_DT} AND SEQ = #{SEQ}) + 1
            , #{STATUS_V}
