            , NOW()
            , #{LOGIN_USER}
            )

mapper.update:
		UPDATE TB_REQ_INVOICE
           SET REQ_DT   = #{REQ_DT  }
             , SEQ      = #{SEQ     }
             , ITEM_CD  = #{ITEM_CD }
             , ITEM_NM  = #{ITEM_NM }
             , AMT_SIGN = #{AMT_SIGN}
             , PER_AMT  = #{PER_AMT }
             , USE_DAY  = #{USE_DAY }
             , UNIT_DAY = #{UNIT_DAY}
             , USE_NUM  = #{USE_NUM }
             , UNIT_NUM = #{UNIT_NUM}
             , TOT_AMT  = #{TOT_AMT }
             , TB_REQ_INVOICE.ORDER  = #{ORDER   }
             , UPD_DTM  = NOW()
             , UPD_ID   = #{LOGIN_USER}
         WHERE REQ_DT  = #{REQ_DT}
           AND SEQ     = #{SEQ}
           AND ITEM_CD = #{PREV_ITEM_CD}

mapper.delete:
DELETE
  FROM TB_REQ_INVOICE
 WHERE 1 = 1
   AND REQ_DT  = #{REQ_DT}
   AND SEQ     = #{SEQ}
   AND ITEM_CD = #{ITEM_CD}

mapper.select:
SELECT COUNT(1) AS CNT
  FROM TB_REQ_PICKUP T1
 WHERE 1 = 1
   AND REQ_DT   = #{REQ_DT}
   AND REQ_SEQ  = #{REQ_SEQ}

