	    UPDATE TB_REQ_PICKUP
           SET PROD_SEQ = #{PROD_SEQ}
             , PER_NUM  = #{PER_NUM}
             , CAR_NUM  = #{CAR_NUM}
             , USE_AMT  = #{USE_AMT}
             , ADD_AMT  = #{ADD_AMT}
             , UPD_DTM  = NOW()
