mapper.select:
SELECT COUNT(1) AS CNT
  FROM TB_REQ_FEE T1
 WHERE 1 = 1
   AND REQ_DT   = #{REQ_DT}
   AND REQ_SEQ  = #{SEQ}

SELECT NVL(TOT_AMT,0) AS TOT_AMT
  FROM TB_REQ_FEE T1
 WHERE 1 = 1
   AND REQ_DT   = #{REQ_DT}
   AND REQ_SEQ  = #{SEQ}

mapper.update:
	    UPDATE TB_REQ_FEE
           SET TOT_AMT = #{TOT_AMT}
             , BAL_AMT = #{TOT_AMT} - NVL(DEP_AMT,0)
             , UPD_DTM  = NOW()
             , UPD_ID   = #{LOGIN_USER}
         WHERE REQ_DT   = #{REQ_DT}
           AND REQ_SEQ  = #{SEQ}

	    UPDATE TB_REQ_FEE
           SET BAL_AMT = BAL_AMT - #{TOT_AMT}
             , TOT_AMT = TOT_AMT - #{TOT_AMT}
             , UPD_DTM  = NOW()
             , UPD_ID   = #{LOGIN_USER}
         WHERE REQ_DT   = #{REQ_DT}
           AND REQ_SEQ  = #{SEQ}

	    UPDATE TB_REQ_BOOKING_M
           SET INV_REG_DT = NOW()
             , UPD_DTM  = NOW()
             , UPD_ID   = #{LOGIN_USER}
         WHERE REQ_DT   = #{REQ_DT}
           AND SEQ  = #{SEQ}

mapper.insert:

INSERT INTO TB_REQ_BOOKING_M
          ( REQ_DT
          , SEQ
          , USER_ID
          , REQ_HAN_NM
          , REQ_ENG_NM
          , REQ_TEL_NO
          , MEM_GBN
          , AGN_CD
          , PRC_STS
          , CHK_IN_DT
          , CHK_OUT_DT
          , ROOM_TYPE
          , FLIGHT_IN
          , FLIGHT_OUT
          , TOT_PERSON
          , R_PERSON
          , N_PERSON
          , NR_PERSON
          , K_PERSON
          , BAS_YY
          , BAS_YY_SEQ
          , PROD_SEQ
          , PICK_GBN
          , ADD_R_S_PER
          , ADD_R_S_DAY
          , ADD_R_S_CNT
          , ADD_R_P_PER
          , ADD_R_P_DAY
          , ADD_R_P_CNT
          , CNT_D1
          , CNT_D2
          , CNT_P1
          , CNT_P2
          , PICK_IN
          , PICK_OUT
          , LATE_CHECK_OUT
          , REMARK
          , INV_REG_DT
          , CONFIRM_NO
          , RND_CHG_YN1
          , RND_CHG_YN2
          , REG_DTM
          , REG_ID
          , UPD_DTM
          , UPD_ID
          )
   VALUES ( #{REQ_DT          }
          , (SELECT NVL(MAX(T1.SEQ),0) AS SEQ FROM TB_REQ_BOOKING_M T1 WHERE T1.REQ_DT = #{REQ_DT}) + 1
          , #{USER_ID         }
          , #{REQ_HAN_NM      }
          , #{REQ_ENG_NM      }
          , #{REQ_TEL_NO      }
          , #{MEM_GBN         }
          , #{AGN_CD          }
          , (CASE WHEN #{MEM_GBN} = '01' THEN '02' WHEN #{MEM_GBN} = '02' THEN '01' ELSE NULL END )
          , #{CHK_IN_DT       }
          , #{CHK_OUT_DT      }
          , #{ROOM_TYPE       }
          , #{FLIGHT_IN       }
          , #{FLIGHT_OUT      }
          , #{TOT_PERSON      }
          , #{R_PERSON        }
          , #{N_PERSON        }
          , #{NR_PERSON       }
          , #{K_PERSON        }
          , (SELECT BAS_YY
               FROM TB_BAS_YY_INFO
              WHERE #{CHK_IN_DT} BETWEEN ST_DT1 AND ED_DT1
                 OR #{CHK_IN_DT} BETWEEN ST_DT2 AND ED_DT2
                 OR #{CHK_IN_DT} BETWEEN ST_DT3 AND ED_DT3)
          , (SELECT BAS_YY_SEQ
               FROM TB_BAS_YY_INFO
              WHERE #{CHK_IN_DT} BETWEEN ST_DT1 AND ED_DT1
                 OR #{CHK_IN_DT} BETWEEN ST_DT2 AND ED_DT2
                 OR #{CHK_IN_DT} BETWEEN ST_DT3 AND ED_DT3)
          , (SELECT PROD_SEQ
               FROM TB_PROD_INFO
              WHERE 1 = 1
               AND BAS_YY_SEQ = (SELECT BAS_YY_SEQ
                                   FROM TB_BAS_YY_INFO
                                  WHERE #{CHK_IN_DT} BETWEEN ST_DT1 AND ED_DT1
                                     OR #{CHK_IN_DT} BETWEEN ST_DT2 AND ED_DT2
                                     OR #{CHK_IN_DT} BETWEEN ST_DT3 AND ED_DT3
                                 )
               AND HDNG_GBN = #{PROD_SEQ}
               AND PROD_COND = (CASE WHEN STR_TO_DATE(#{CHK_OUT_DT},'%Y%m%d') - STR_TO_DATE(#{CHK_IN_DT}, '%Y%m%d') >= 10 THEN 'DU10'
                                     WHEN STR_TO_DATE(#{CHK_OUT_DT},'%Y%m%d') - STR_TO_DATE(#{CHK_IN_DT}, '%Y%m%d') <  10 THEN 'DD10' END)
             )
          , '01'
          , #{ADD_R_S_PER     }
          , #{ADD_R_S_DAY     }
          , #{ADD_R_S_CNT     }
          , #{ADD_R_P_PER     }
          , #{ADD_R_P_DAY     }
          , #{ADD_R_P_CNT     }
          , #{CNT_D1          }
          , #{CNT_D2          }
          , #{CNT_P1          }
          , #{CNT_P2          }
          , #{PICK_IN         }
          , #{PICK_OUT        }
          , #{LATE_CHECK_OUT  }
          , #{REMARK          }
          , #{INV_REG_DT      }
          , #{CONFIRM_NO      }
          , #{RND_CHG_YN1     }
          , #{RND_CHG_YN2     }
          , NOW()
          , #{LOGIN_USER}
          , NOW()
          , #{LOGIN_USER      }
          )
