mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY T1.HAN_NAME asc) ROWNUM ,T1.USER_ID ,T1.MEMBER_ID ,T1.MEM_GBN ,T1.HAN_NAME ,T1.ENG_NAME ,T1.TEL_NO ,T1.EMAIL ,T1.PASSWD ,T1.AGENCY_NAME ,T1.AGENCY_GB ,T1.RET_YN ,T1.PERINFO_AGREE_YN ,T1.REG_ID ,T1.REG_DTM ,T1.UPD_ID ,T1.UPD_DTM ,( SELECT CODE_NM FROM BC_COMM_L WHERE 1=1 AND HEAD_CD = '500030' AND CODE = T1.MEM_GBN ) AS MEM_NM FROM tb_member as T1 left JOIN bc_user as T2 ON T2.USER_ID = T1.USER_ID WHERE 1 = 1
AND T1.USER_ID LIKE CONCAT('%',#{S_USER_ID},'%')
AND ((T1.HAN_NAME LIKE CONCAT('%',#{S_USER_NM},'%')) OR (T1.ENG_NAME LIKE CONCAT('%',#{S_USER_NM},'%')))
AND T1.MEM_GBN = #{S_MEM_GBN}
AND T1.USER_ID = #{USER_ID}

	    SELECT T1.CODE , T1.CODE_NM
          FROM BC_COMM_L T1
          JOIN BC_COMM_H T2 ON T2.HEAD_CD = /**/ T1.HEAD_CD
         WHERE 1 = 1
           AND T1.HEAD_CD = #{HEAD_CD}

	  	   AND T1.REF_CHR1 = #{REF_CHR1}


	  	   AND T1.CODE > #{CODE}



	           ORDER BY T1.REF_CHR5


               ORDER BY T1.CODE

SELECT CODE    AS CODE
     , CODE_NM AS VALUE
  FROM BC_COMM_L
 WHERE 1 = 1

   AND HEAD_CD  = #{HEAD_CD}


   AND REF_CHR1 = #{REF_CHR1}


   AND REF_CHR2 = #{REF_CHR2}

SELECT COUNT(1)
  FROM TB_MEMBER 
 WHERE USER_ID = #{USER_ID}

mapper.update:
UPDATE TB_MEMBER
   SET HAN_NAME  = #{HAN_NAME}
	  ,ENG_NAME  = #{ENG_NAME}
	  ,TEL_NO    = #{TEL_NO}
	  ,MEMBER_ID = #{MEMBER_ID}
	  ,EMAIL     = #{EMAIL}
	  ,AGENCY_NAME = #{AGENCY_NAME}
	  ,AGENCY_GB   = #{AGENCY_GB}
	  ,PERINFO_AGREE_YN = #{PERINFO_AGREE_YN}
	  ,RET_YN    = #{RET_YN}
	  ,UPD_ID    = #{LOGIN_USER}
	  ,UPD_DTM   = NOW()
WHERE USER_ID    = #{USER_ID}

mapper.select:
