WHERE MEMBER_ID = #{Ex_MEMBER_ID}
  AND HAN_NAME  = #{Ex_HAN_NAME}
  AND ENG_NAME  = #{Ex_ENG_NAME}
  AND TEL_NO    = #{Ex_TEL_NO}

mapper.delete:
