mapper.update:
UPDATE BC_USER
   SET PW_CURR = #{NEW_PASSWORD}
	  ,PW_PAST1 = PW_CURR
	  ,PW_PAST2 = PW_PAST1
	  ,PW_PAST3 = PW_PAST2

 	  ,LOGIN_DT = NOW()

 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}

mapper.select:
SELECT COUNT(1)
  FROM BC_USER 
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}
   AND (PW_CURR = #{NEW_PASSWORD}
    OR PW_PAST1 = #{NEW_PASSWORD}
	OR PW_PAST2 = #{NEW_PASSWORD}
	OR PW_PAST3 = #{NEW_PASSWORD})

UPDATE BC_USER 
   SET LOCK_YN = 'N',
       WRONG_CNT = 0
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}

