mapper.select:
SELECT T1.CODE AS AGENCY_COMP_CD , T1.CODE_NM AS AGENCY_COMP , T1.REF_CHR1 AS AGENCY_GBN_CD , T3.CODE_NM AS AGENCY_GBN , DATE_FORMAT(T1.REG_DT, '%Y-%m-%d') AS REG_DATE FROM BC_COMM_L T1 JOIN BC_COMM_H T2 ON T2.HEAD_CD = /**/ T1.HEAD_CD JOIN BC_COMM_L T3 ON T1.REF_CHR1 = T3.CODE AND T3.HEAD_CD = 500260 WHERE 1 = 1 AND T1.HEAD_CD = 500120
AND T1.REF_CHR1 = #{AGENCY_GBN_CD}
ORDER BY T1.REF_CHR1 , T1.CODE_NM

mapper.delete:
DELETE FROM BC_COMM_L
 WHERE HEAD_CD = 500120
   AND CODE  = #{AGENCY_COMP_CD}

mapper.update:
