mapper.select:
SELECT COUNT(1)
  FROM TB_MEMBER 
 WHERE USER_ID = #{USER_ID}

mapper.update:
UPDATE TB_MEMBER
   SET HAN_NAME = #{HAN_NAME}
	  ,ENG_NAME = #{ENG_NAME}
	  ,TEL_NO = #{TEL_NO}
	  ,EMAIL = #{EMAIL}
	  ,PASSWD = #{PASSWD}
	  ,RET_YN = #{RET_YN}
	  ,UPD_DTM = NOW()
WHERE USER_ID = #{USER_ID}

mapper.select:
SELECT COUNT(1)
  FROM tb_admin_reg_member 
 WHERE HAN_NAME = #{HAN_NAME}
   AND ENG_NAME = #{ENG_NAME}
   AND TEL_NO = #{TEL_NO}

SELECT COUNT(1)
  FROM tb_admin_reg_member 
 WHERE HAN_NAME = #{Ex_HAN_NAME}
   AND ENG_NAME = #{Ex_ENG_NAME}
   AND TEL_NO = #{Ex_TEL_NO}

mapper.update:
UPDATE tb_admin_reg_member
   SET HAN_NAME = #{HAN_NAME}
	  ,ENG_NAME = #{ENG_NAME}
	  ,TEL_NO = #{TEL_NO}
	  ,RET_YN = #{RET_YN}
	  ,UPD_DTM = NOW()
WHERE HAN_NAME = #{Ex_HAN_NAME}
  AND ENG_NAME = #{Ex_ENG_NAME}
  AND TEL_NO = #{Ex_TEL_NO}

mapper.delete:
DELETE FROM tb_admin_reg_member
 WHERE HAN_NAME = #{HAN_NAME}
   AND ENG_NAME = #{ENG_NAME}
   AND TEL_NO = #{TEL_NO}

mapper.update:
UPDATE BC_USER
   SET PW_CURR = #{NEW_PASSWORD}
	  ,PW_PAST1 = PW_CURR
	  ,PW_PAST2 = PW_PAST1
	  ,PW_PAST3 = PW_PAST2

 	  ,LOGIN_DT = NOW()

 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}

mapper.select:
SELECT COUNT(1)
  FROM BC_USER 
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}
   AND (PW_CURR = #{NEW_PASSWORD}
    OR PW_PAST1 = #{NEW_PASSWORD}
	OR PW_PAST2 = #{NEW_PASSWORD}
	OR PW_PAST3 = #{NEW_PASSWORD})

UPDATE BC_USER 
   SET LOCK_YN = 'N',
       WRONG_CNT = 0
 WHERE COMP_CD = #{COMP_CD}
   AND USER_ID = #{USER_ID}
